CREATE TABLE ROUTE (ID NUMBER(10) NOT NULL, BUS NUMBER(1) default 0 NULL, LATE NUMBER(10) DEFAULT 0 NOT NULL, NAME VARCHAR2(255) NULL UNIQUE, TRAIN NUMBER(1) default 0 NULL, VERSION NUMBER(10) DEFAULT 1 NOT NULL, PRIMARY KEY (ID))
CREATE TABLE PERSON (ID NUMBER(10) NOT NULL, NAME VARCHAR2(255) NULL, PASSWORD VARCHAR2(255) NULL, VERSION NUMBER(10) DEFAULT 1 NOT NULL, PRIMARY KEY (ID))
CREATE TABLE TIMETABLE (ID NUMBER(10) NOT NULL, HOLIDAY NUMBER(1) default 0 NULL, START_TIME NUMBER(10) NULL, VERSION NUMBER(10) DEFAULT 1 NOT NULL, WEEKEND NUMBER(1) default 0 NULL, ROUTE_FK NUMBER(10) NULL, STATION_FK NUMBER(10) NULL, PRIMARY KEY (ID))
CREATE TABLE STATION (ID NUMBER(10) NOT NULL, BUS NUMBER(1) default 0 NULL, NAME VARCHAR2(255) NULL UNIQUE, TRAIN NUMBER(1) default 0 NULL, VERSION NUMBER(10) DEFAULT 1 NOT NULL, PRIMARY KEY (ID))
CREATE TABLE LINE (ID NUMBER(10) NOT NULL, BUS NUMBER(1) default 0 NULL, COST NUMBER(10) NULL, LENGTH NUMBER(10) NULL, ORDINAL NUMBER(10) NULL, TIME NUMBER(10) NULL, TRAIN NUMBER(1) default 0 NULL, VERSION NUMBER(10) DEFAULT 1 NOT NULL, ROUTE_FK NUMBER(10) NULL, START_STATION_FK NUMBER(10) NULL, STOP_STATION_FK NUMBER(10) NULL, PRIMARY KEY (ID))
ALTER TABLE LINE ADD CONSTRAINT UNQ_LINE_0 UNIQUE (ROUTE_FK, ORDINAL)
ALTER TABLE TIMETABLE ADD CONSTRAINT FK_TIMETABLE_STATION_FK FOREIGN KEY (STATION_FK) REFERENCES STATION (ID)
ALTER TABLE TIMETABLE ADD CONSTRAINT FK_TIMETABLE_ROUTE_FK FOREIGN KEY (ROUTE_FK) REFERENCES ROUTE (ID)
ALTER TABLE LINE ADD CONSTRAINT FK_LINE_START_STATION_FK FOREIGN KEY (START_STATION_FK) REFERENCES STATION (ID)
ALTER TABLE LINE ADD CONSTRAINT FK_LINE_ROUTE_FK FOREIGN KEY (ROUTE_FK) REFERENCES ROUTE (ID)
ALTER TABLE LINE ADD CONSTRAINT FK_LINE_STOP_STATION_FK FOREIGN KEY (STOP_STATION_FK) REFERENCES STATION (ID)
CREATE SEQUENCE ALWARA_SEQUENCE START WITH 1
